import axios from 'axios'

export async function apiRequest(method, url, body = {}) {

    let promise;
    switch (method) {
        case "GET":
            promise = new Promise((resolve,reject) => {
                axios.get(url)
                .then((res) => {
                    console.log(res);
                    // let result = {
                    //     data: res.data,
                    //     isError: false,
                    //     status: res.status
                    // }
                    // resolve(result)
                })
                .catch((err) => {
                    let result = {
                        isError: true,
                        error: err
                    }
                    resolve(result)
                })
            })
            return promise;

        case "POST":
            break;
        case "PUT":
            break;
        case "DELETE":
            break;
        default: 
            break;
    }
}
