import React, { useState } from 'react'
import { Button, Col, Form, Row } from 'reactstrap'
import './styles.scss';
import '../user/style.scss'
import { useHistory } from 'react-router';

const AddUserForm = ({props}) => {
    const history = useHistory();
    const [formData, setformData] = useState({
        name:'',
        contactNo: '',
        dob:'',
        email: '',
        role: ''
    })

    const onValuChange = (label,value) => {
        setformData({
            ...formData,
            [label]: value
        });
    }

    return (
        <div className="adduser-form">
            <Row>
                <Col>
                    <p className="page-head text-center">Add User</p>
                </Col>
            </Row>
            <Form className="form-padding">
                <Row className="d-flex space-between">
                    <Col><label>Name : </label></Col>
                    <Col><input type='text' value={formData.name} maxLength="30" className="custom.form-control" onChange={(e) => {
                        onValuChange("name", e.target.value);
                    }}  /></Col>
                </Row>
                <Row className="d-flex space-between">
                    <Col><label>Email : </label></Col>
                    <Col><input type='email' value={formData.email} className="custom.form-control" onChange={(e) => {
                        onValuChange("email", e.target.value);
                    }} /></Col>
                </Row>
                <Row className="d-flex space-between">
                    <Col><label>Contact No : </label></Col>
                    <Col><input type='text' value={formData.contactNo} className="custom.form-control" onChange={(e) => {
                        onValuChange("contactNo", e.target.value);
                    }} /></Col>
                </Row>
                <Row className="d-flex space-between">    
                    <Col><label>Datet of Birth : </label></Col>
                    <Col><input type='date' value={formData.dob} className="custom.form-control" onChange={(e) => {
                        onValuChange("dob", e.target.value);
                    }}  /></Col>
                </Row>
                <Row className="d-flex space-between">
                    <Col><label>Role : </label></Col>
                    <Col></Col>
                </Row>
                <Row>
                    <Button onClick={ () => {
                        history.push('/users')
                    }}>Cancel</Button>
                    <Button onClick={() => {

                    }} >Submit</Button>
                </Row>
            </Form>
        </div>
    )
}

export default AddUserForm
