import { USERS_CONSTANTS } from "./action.type";
import { URLS } from "../../../constants/urls";
import { apiRequest } from "../../../components/common/apiRequests";

const loadRequest = (pagination = false) => ({
  type: USERS_CONSTANTS.USERS_LIST_LOAD_REQUEST,
  pagination,
});
const loadFailure = (payload = "error") => ({
  type: USERS_CONSTANTS.USERS_LIST_LOAD_FAILURE,
  payload,
});
const loadSuccess = (payload) => {
  let data = JSON.parse(payload.data).result;
  return {
    type: USERS_CONSTANTS.USERS_LIST_LOAD_SUCCESS,
    data: data.presos,
    stats: data.stats,
    vendors: data.vendors,
  };
};
export const getUsersLists = (criteria, pagination = false) => {
  let url = URLS.UserListURL;

  return (dispatch) => {
    dispatch(loadRequest(pagination));
    apiRequest("GET", url)
      .then((result) => {
        console.log("Get users List", result);
        if (data.isError) {
          dispatch(loadFailure("Something went Worng"));
        } else {
          dispatch(loadSuccess(result.item));
        }
      })
      .catch((err) => {
        console.log("Get user list error", err);
        dispatch(loadFailure("Something went Worng"));
      });
  };
};
