import { PRESENTATION_LIST_CONSTANTS } from './action.type'
import moment from 'moment'
import { FORMATS } from 'utils/constants'

const initialState = {
  isLoaded: false,
  isError: false,
  errorMessage: '',
  list: [],
}

const defaultFilter = { ...initialState.filters }

const presentationList = (state = initialState, action = {}) => {
  switch (action.type) {
    case PRESENTATION_LIST_CONSTANTS.PRESENTATION_LOAD_REQUEST:
      return {
        ...state,
        isLoaded: false,
        isError: false,
        errorMessage: '',
        list: [],
      }

    case PRESENTATION_LIST_CONSTANTS.PRESENTATION_LOAD_SUCCESS:
      return {
        ...state,
        isLoaded: true,
        isError: false,
        errorMessage: '',
        list: action.data,
      }

    case PRESENTATION_LIST_CONSTANTS.PRESENTATION_LOAD_FAILURE:
      return {
        ...state,
        isLoaded: true,
        isError: true,
        errorMessage: action.payload
      }
    default:
      return state
  }
}
export default presentationList
